            # file_path = os.path.join(output_dir, f"{dataset}_{metric_name}.jpeg")
            # plt.savefig(file_path)
            

            plt.figure(figsize=(8, 6))
            plt.title(f'{dataset} - {metric_name} vs FL Rounds')
            plt.xlabel('FL Rounds')
            plt.ylabel(metric_name)
            if "loss" in metric_name.lower():
                plt.ylim(0, 4)
            else:
                plt.ylim(0, 1)
            all_round_nums = [round_data[0] for round_data in rounds]
            all_metric_values = [round_data[1][0][metric_name] for round_data in rounds]
            

            plt.plot(all_round_nums, all_metric_values, linestyle='-', color='blue')
            file_path = os.path.join(output_dir, f"{dataset}_{metric_name}.jpeg")
            plt.savefig(file_path)
            plt.clf()
